import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { Layout } from './components/layout/Layout';
import { HomePage } from './components/pages/HomePage';
import { MenuPage } from './components/pages/MenuPage';
import { GalleryPage } from './components/pages/GalleryPage';
import { BookTablePage } from './components/pages/BookTablePage';
import { Footer } from './components/layout/Footer';

// Wrapper to allow using useLocation hook inside Layout if needed, 
// or just to ensure scroll to top on route change
function ScrollToTop() {
  const { pathname } = useLocation();

  React.useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  return null;
}

function App() {
  return (
    <Router>
      <ScrollToTop />
      <Layout>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/menu" element={<MenuPage />} />
          <Route path="/gallery" element={<GalleryPage />} />
          <Route path="/book" element={<BookTablePage />} />
        </Routes>
        <Footer />
      </Layout>
    </Router>
  );
}

export default App;

